"use strict";(self.webpackChunkenumerology=self.webpackChunkenumerology||[]).push([[652],{5393:function(e,o,n){n.r(o),n.d(o,{Head:function(){return m}});var t=n(1151),a=n(7294),l=n(3242),s=n(9357);const i=l.Z;function m(e){return a.createElement(s.p,{title:e.pageContext.frontmatter.title})}function r(e){const o=Object.assign({h1:"h1",h2:"h2",p:"p",ul:"ul",li:"li"},(0,t.ah)(),e.components);return a.createElement(a.Fragment,null,a.createElement(o.h1,null,"Číslo 8"),"\n",a.createElement(o.h2,null,"Charakteristika čísla osm"),"\n",a.createElement(o.p,null,"Osmička je nejmocnější ze všech čísel. Je to číslo peněz, ambicí, vůle, logického myšlení, obrazotvornosti a organizačních schopností. Značí zájem o hmotný svět, zaměření na získávání majetku, peněz, na úspěch a zisk. Symbolizuje i touhu po moci, slávě, vlivu a sexu."),"\n",a.createElement(o.p,null,"Je to číslo vyjadřující osud, rovnováhu a spravedlnost. V povaze člověka představuje moc, sílu, zdatnost, vůdcovské a organizační schopnosti, ctižádost, cílevědomost, bojovnost, houževnatost, schopnost zdolávat překážky, pracovitost a píli, ale také bezohlednost, prudkost, strohost, panovačnost, manipulaci s ostatními nebo agresivitu."),"\n",a.createElement(o.h2,null,"Osmička jako číslo měsíce nebo roku"),"\n",a.createElement(o.p,null,"Pod vlivem osmičky jsou lidé narození v srpnu a v roce, jehož součet dává číslo osm. Jde např. o roky 1961, 1970, 1979, 1988, 1997, 2006. Velké množství lidí s číslem osm jako číslem měsíce nebo roku mají hodně vyhraněný vztah k jednomu nebo oběma svým rodičům."),"\n",a.createElement(o.p,null,"Jelikož osmička je číslo nekompromisní, lidé s osmičkou jako číslem měsíce nebo roku často jednoho nebo oba rodiče zbožňují, či se naopak s nimi nesnesou nebo si jich neváží. Často jim trvá větší část života, než tuto situaci dokáží nějak zpracovat. Stává se ale, že si vyberou partnera s povahovými rysy, které v obdobné podobě má i rodič, s nímž mají problém."),"\n",a.createElement(o.p,null,"V mnoha případech jsou to ale i rodiče, kteří ve vztahu ke svým „osmičkovým“ dětem nedokáží urovnat neshody. Lidé s číslem měsíce nebo roku osm jsou silní, houževnatí a cílevědomí a proto jsou schopni dosáhnout čehokoliv."),"\n",a.createElement(o.p,null,"V partnerském vztahu dokáží být druhému oporou a jistotou, dokáží být ale i náladoví a vznětliví. Jejich organizační a vůdcovské schopnosti jsou mimořádné a vše co dělají se snaží dělat dokonale."),"\n",a.createElement(o.h2,null,"Osmička jako životní (osudové) číslo"),"\n",a.createElement(o.p,null,"Osoba jejíž životním číslem je číslo osm je ctižádostivá, toužící po úspěchu, moci a penězích. Disponuje organizačními a řídícími schopnostmi, je vytrvalá, houževnatá a konzervativní. Životní cesta tohoto člověka bývá často obtížná a plná překážek. Nicméně tato osoba má v sobě zároveň sílu a vůli překážky překonávat. Téma života lidí s životním číslem osm je rovnováha a karma – zákon příčiny a následku."),"\n",a.createElement(o.p,null,"Tito lidé nenaleznou štěstí, pokud nebudou zachovávat rovnováhu mezi příčinou a následkem, dáváním a braním. Někteří z lidí s životním číslem osm rádi hromadí bohatství a materiální statky. Životním úkolem lidí s životním číslem osm je nalézt rovnováhu mezi materiálním a duchovním světem a uvědomit si, že materiální bohatství jim nepřinese uspokojení, pokud nebude sloužit ku prospěchu ostatních."),"\n",a.createElement(o.p,null,"Ovlivnění číslem osm se může v negativní podobě projevit ve formě útočnosti, tvrdosti, bezohlednosti, přílišné dominance, nesnášenlivosti, zneužíváním moci, náladovostí či velkým míněním o sobě."),"\n",a.createElement(o.h2,null,"Použitá literatura"),"\n",a.createElement(o.ul,null,"\n",a.createElement(o.li,null,"František Krum – Základy numerologie – věštění z čísel a karet"),"\n",a.createElement(o.li,null,"Robin Steinová – Numerologie – čísla lásky"),"\n",a.createElement(o.li,null,"Anna Šanová – Velká kniha Numerologie – učebnice numerologie pro každého"),"\n",a.createElement(o.li,null,"Jules Silver – Numerologie – Magie a mystika čísel"),"\n",a.createElement(o.li,null,"Milan Walek – Numerologie v praxi – Tajná řeč čísel"),"\n",a.createElement(o.li,null,"Dawne Kovan – Tajemství numerologie"),"\n",a.createElement(o.li,null,"Robert Griesbeck – Učebnice numerologie – Esoterika a Astrologie v Numerologii"),"\n",a.createElement(o.li,null,"Jitka Kadlecová – Jméno a numerologie"),"\n"))}o.default=function(e){return void 0===e&&(e={}),a.createElement(i,e,a.createElement(r,e))}}}]);
